import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import { rupiah } from '../../utils/icons';
import Chart from '../Chart/Chart';
import History from '../History/History';
import currencyFormat from 'currency-formatter';

function Dashboard() {
  const { totalExpenses, incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses } = useGlobalContext();

  useEffect(() => {
    getIncomes();
    getExpenses();
  }, []);

  return (
    <DashboardStyled>
      <InnerLayout>
        <h1>Semua Transaksi</h1>
        <div className="stats-con">
          <div className="dash-con">
            <div className="chart-con">
              <Chart />
            </div>
            <div className="history-con">
              <div className="left">
                <History />
                <h2 className="salary-title">
                  Min <span>Pemasukan</span>Maks
                </h2>
                <div className="salary-item">
                  <div className="min-cur">{Math.min(...incomes.map((item) => item.amount))}</div>
                  <div className="max-cur">{Math.max(...incomes.map((item) => item.amount))}</div>
                </div>
                <h2 className="salary-title">
                  Min <span>Pengeluaran</span>Maks
                </h2>
                <div className="salary-item">
                  <p>{Math.min(...expenses.map((item) => item.amount))}</p>
                  <p>{Math.max(...expenses.map((item) => item.amount))}</p>
                </div>
              </div>
            </div>
            <div className="right">
              <div className="income">
                <h2>Total Pemasukan</h2>
                <p>{totalIncome()}</p>
              </div>
              <div className="expense">
                <h2>Total Pengeluaran</h2>
                <p>{totalExpenses()}</p>
              </div>
              <div className="balance">
                <h2>Total Saldo</h2>
                <p>{totalBalance()}</p>
              </div>
            </div>
          </div>
        </div>
      </InnerLayout>
    </DashboardStyled>
  );
}

const DashboardStyled = styled.div`
.stats-con {
  display: flex;
  gap: 5rem;
  flex-direction: column;
  .min-cur, .max-cur {
    background: #fcf6f9;
    border: 2px solid #ffffff;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
  }
.min-cur {
  text-align: left;
}
.max-cur {
  text-align: right;
}
  .dash-con {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 2rem;
  }
`;

export default Dashboard;
