import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { Doughnut } from 'react-chartjs-2';
import styled from 'styled-components';

ChartJS.register(ArcElement, Tooltip, Legend);

const fillPattern = ctx.createPattern(img, 'repeat');

const ChartDoughnut = ({ income, expense, balance }) => {
  const data = {
    labels: ['Pemasukan', 'Pengeluaran', 'Saldo'],
    datasets: [
      {
        label: 'Total',
        data: [income, expense, balance()],
        backgroundColor: [pattern.draw('square', '#ff6384'), pattern.draw('circle', '#36a2eb'), pattern.draw('diamond', '#cc65fe'), pattern.draw('triangle', '#ffce56')],
        borderColor: 'transparent',
        hoverOffset: 4,
      },
    ],
  };
  return (
    <DoughnutStyled>
      <Doughnut data={data} />
    </DoughnutStyled>
  );
};

const DoughnutStyled = styled.div`
  canvas: {
    border: transparent;
    width: 100%;
  }
`;

export default ChartDoughnut;
